import React, { useEffect, useState } from 'react'
import "./EachProduct.js"
import EachProduct from './EachProduct.js'
import Form from "../Form/Form.js"


const Products = ({navbarRefresh}) => {
	useEffect( () => {
		navbarRefresh()} , [] )

    const [products, setProducts] = useState([
		{
			id: 1,
			name: "Product1",
			price: 1000,
			quantity: 10,
			imageUrl: "data:image/jpeg;base64,"
		},
		{
			id: 2,
			name: "Product2",
			price: 2000,
			quantity: 20,
			imageUrl: "data:image/jpeg;base64,"
		},
		{
			id: 3,
			name: "Product3",
			price: 3000,
			quantity: 30,
			imageUrl: "data:image/jpeg;base64,"
		},
		{
			id: 4,
			name: "Product4",
			price: 4000,
			quantity: 40,
			imageUrl: "data:image/jpeg;base64,"
		}
	]);

	const [showForm, setShowForm] = useState(false);
	const [productForUpdate, setProductForUpdate] = useState(null)

	const onClickAdd = () => {
		setShowForm(!showForm)
	}

	const onAddProduct = (name, price, quantity) => {
		if (!name || !price || !quantity) {
			alert("Please enter complete product details")
			return
		}
		let obj = {
			id: name + price.toString() + quantity.toString(),
			name: name,
			price: price,
			quantity: quantity,
			imageUrl: "data:image/jpeg;base64,"
		}
		setProducts([...products,obj])
	}

	const onDeleteProduct = (id) => {
		console.log("delete = " + id)
		let remProducts = products.filter((product) => product.id != id)
		setProducts(remProducts)
	}

	const onUpdateProduct = (product) => {
		setProductForUpdate(product)
		setShowForm(true)
	}

	const onUpdateProductForm = (id, name, price, quantity) => {
		if (!name || !price || !quantity) {
			alert("Please enter complete product details")
			return
		}

		console.log("id = " + id)
		let index = products.findIndex((product) => product.id == id)
		console.log("index = " + index)
		var allProducts = products
		var product = allProducts[index]
		console.log(product)
		product.name = name
		product.price = price
		product.quantity = quantity
		allProducts[index] = product
		setProducts(allProducts)
		setShowForm(false)
		setProductForUpdate(null)
	}

  return (
    <div className='container'>
		<div class="flex-box">
			<button type="button" className="button" onClick={onClickAdd}>+ Add</button>
		</div>
		{showForm ? <Form onAddProduct={onAddProduct} product={productForUpdate} onUpdateProductForm={onUpdateProductForm}/> : null}
		<div>
		{
			products.map(product => {
				return <EachProduct product = {product} onDeleteProduct={onDeleteProduct} onUpdateProduct={onUpdateProduct}/>
			})
		}
		</div>
    </div>
  )
}

export default Products
